import "./index.css";
import React, { useState } from 'react';

function App() {
  // Estado para controlar la animación
  const [isAnimating, setIsAnimating] = useState(false);

  // Función que se ejecuta al hacer clic en el botón
  const handleClick = () => {
    setIsAnimating(true); // Activa la animación
    setTimeout(() => setIsAnimating(false), 1000); // Desactiva la animación después de 1 segundo
  };

  return (
    <div className={`min-h-screen flex flex-col transition-colors duration-500 ${isAnimating ? 'bg-gradient-to-r from-purple-500 to-pink-500' : 'bg-gray-100'}`}>
      {/* Encabezado */}
      <header className="bg-blue-600 text-white p-4 shadow-lg">
        <div className="container mx-auto">
          <h1 className="text-2xl font-bold">Mi Proyecto con Tailwind</h1>
        </div>
      </header>

      {/* Contenido principal */}
      <main className="flex-grow container mx-auto p-4">
        <section className="bg-white p-6 rounded-lg shadow-md">
          <h2 className="text-xl font-semibold mb-4">¡Bienvenido!</h2>
          <p className="text-gray-700">
            Este es un ejemplo de cómo usar Tailwind CSS en un proyecto de React.
          </p>
          <button
            onClick={handleClick}
            className={`mt-4 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition duration-300 ${isAnimating ? 'animate-bounce' : ''}`}
          >
            Haz clic aquí
          </button>
        </section>
      </main>

      {/* Pie de página */}
      <footer className="bg-blue-600 text-white p-4 mt-auto">
        <div className="container mx-auto text-center">
          <p>&copy; 2023 Mi Proyecto. Todos los derechos reservados.</p>
        </div>
      </footer>
    </div>
  );
}

export default App;
