import { useState, useEffect, useRef } from 'react';
import './App.css';

// === DoubleLinkedList ===
const CAPACITY = 3;

class Node {
  constructor(data) {
    this.prev = null;
    this.next = null;
    this.data = data;
  }
}

class DoubleLinkedList {
  constructor() {
    this.head = null;
    this.tail = null;
    this.size = 0;
    this.capacity = CAPACITY;
    this.current = null;
  }

  add(data) {
    const newNode = new Node(data);

    if (!this.head) {
      this.head = newNode;
      this.tail = newNode;
      this.size++;
    } else if (this.size !== this.capacity) {
      newNode.next = this.head;
      this.head.prev = newNode;
      this.head = newNode;
      this.size++;
    } else {
      this.tail = this.tail.prev;
      this.tail.next = null;

      newNode.next = this.head;
      this.head.prev = newNode;
      this.head = newNode;
    }

    this.current = null; // Reset historial al agregar nuevo
  }

  up() {
    if (!this.head) return null;

    if (!this.current) {
      this.current = this.head;
    } else if (this.current.next) {
      this.current = this.current.next;
    }

    return this.current.data;
  }

  down() {
    if (!this.head) return null;

    if (!this.current) {
      this.current = this.head;
    } else if (this.current.prev) {
      this.current = this.current.prev;
    }

    return this.current.data;
  }
}

// === App ===
function App() {
  const [input, setInput] = useState("");
  const [error, setError] = useState("");
  const historial = useRef(new DoubleLinkedList());

  const parseExpression = (expr) => {
    expr = expr.replace(/\s+/g, '');

    if (!isNaN(expr) && !isNaN(parseFloat(expr))) {
      return { type: 'number', value: parseFloat(expr) };
    }

    const operators = [
      { ops: ['+', '-'], precedence: 1 },
      { ops: ['*', '/'], precedence: 2 }
    ];

    let splitPos = -1;
    let currentOp = null;

    for (const group of operators) {
      for (let i = expr.length - 1; i >= 0; i--) {
        const char = expr[i];
        if (group.ops.includes(char)) {
          splitPos = i;
          currentOp = char;
          break;
        }
      }
      if (splitPos !== -1) break;
    }

    if (splitPos === -1) throw new Error('Expresión inválida');

    const leftExpr = expr.substring(0, splitPos);
    const rightExpr = expr.substring(splitPos + 1);

    return {
      type: 'operation',
      operator: currentOp,
      left: parseExpression(leftExpr),
      right: parseExpression(rightExpr)
    };
  };

  const evaluate = (node) => {
    if (node.type === 'number') return node.value;

    const left = evaluate(node.left);
    const right = evaluate(node.right);

    switch (node.operator) {
      case '+': return left + right;
      case '-': return left - right;
      case '*': return left * right;
      case '/':
        if (right === 0) throw new Error("División por cero");
        return left / right;
      default: throw new Error('Operador desconocido');
    }
  };

  const calcular = () => {
    try {
      const expr = String(input);
      const parsed = parseExpression(expr);
      const result = evaluate(parsed);

      if (!isFinite(result)) {
        setError("Error: división por cero");
      } else {
        const resultado = result.toString();
        setInput(resultado);
        setError("");
        historial.current.add(expr); // Guardar solo la expresión ingresada
      }
    } catch (error) {
      setError("Error: expresión inválida");
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      calcular();
    } else if (e.key === "ArrowUp") {
      const upValue = historial.current.up();
      if (upValue !== null) setInput(upValue);
    } else if (e.key === "ArrowDown") {
      const downValue = historial.current.down();
      if (downValue !== null) setInput(downValue);
    }
  };

  return (
    <div className="flex flex-col h-screen">
      <header className="bg-orange-400 shadow text-white text-4xl py-6">
        <h1 className="font-black text-center">Calculadora - V3</h1>
      </header>

      <div className="flex-1 flex items-center justify-center">
        <div className="bg-gray-50 flex flex-col border rounded-lg border-4 border-orange-400 p-8 w-3/4 h-3/5 items-center">

          {/* Input */}
          <ul className="w-4/5 mx-auto flex items-center gap-2">
            <li>
              <button
                className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
                onClick={() => {
                  setInput("");
                  setError("");
                }}>
                AC
              </button>
            </li>
            <li className="flex-grow">
              <input
                className="w-full border border-gray-300 p-2 rounded"
                placeholder="Ingrese expresión"
                type="text"
                value={input}
                onChange={(e) => {
                  setInput(e.target.value);
                  if (error) setError("");
                }}
                onKeyDown={handleKeyDown}
              />
            </li>
            <li>
              <button
                className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600"
                onClick={calcular}>
                Enter
              </button>
            </li>
          </ul>

          <p className="mt-4 text-red-500 font-bold">{error}</p>

          {/* Botones */}
          <div className="mt-6 flex flex-col justify-center items-center w-3/4 h-3/4 gap-4 text-white">
            {[
              ["1", "2", "3", "+"],
              ["4", "5", "6", "-"],
              ["7", "8", "9", "*"],
              [".", "0", "←", "/"]
            ].map((row, i) => (
              <div key={i} className="flex gap-4">
                {row.map((val) => (
                  <button
                    key={val}
                    onClick={() => {
                      if (val === "←") {
                        setInput(input.slice(0, -1));
                      } else {
                        setInput(input + val);
                      }
                    }}
                    className={`w-16 h-16 flex items-center justify-center ${
                      ["+", "-", "*", "/", ".", "←"].includes(val)
                        ? "bg-red-400 hover:bg-red-600"
                        : "bg-orange-400 hover:bg-orange-600"
                    } rounded text-2xl font-bold`}>
                    {val}
                  </button>
                ))}
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
